import React from "react";
import { connect } from "react-redux";
import { VictoryPie, VictoryTheme } from "victory";
import { fetchTasks } from "../../actions";

class TaskGraph extends React.Component {
  componentDidMount() {
    this.props.fetchTasks();
  }

  createCategoriesArray() {}

  renderGraph = () => {
    const userTasks = this.props.tasks.filter(
      task => task.userId === this.props.currentUserId
    );

    console.log(userTasks);

    const categories = userTasks.map(task => task["category"]);
    const numTasks = userTasks.length;
    const running = Math.floor(
      (userTasks.filter(task => task["category"] === "Running").length /
        numTasks) *
        100
    );

    if (!this.props.tasks) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <VictoryPie
            data={[
              { x: categories[0], y: running },
              { x: categories[1], y: running }
            ]}
            // colorScale={["tomato", "orange", "gold", "cyan", "navy" ]}
            innerRadius={40}
            padAngle={1}
            height={225}
            theme={VictoryTheme.material}
          />
        </div>
      );
    }
  };

  render() {
    return <div>{this.renderGraph()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    tasks: Object.values(state.tasks),
    currentUserId: state.auth.userId
  };
};

export default connect(
  mapStateToProps,
  { fetchTasks }
)(TaskGraph);
