import React from "react";
import { connect } from "react-redux";
import { VictoryPie } from "victory";
import { fetchTasks } from "../../actions";

class TaskGraph extends React.Component {
  componentDidMount() {
    this.props.fetchTasks();
  }

  createCategoriesArray() {

  }

  renderGraph = () => {
    const userTasks = this.props.tasks.filter(
      task => task.userId !== this.props.currentUserId
    );

    const categories = userTasks.map((task) => task['category']);

    return (
      <div>
        <VictoryPie
          data={[
            { x: categories[0], y: 10 },
            { x: categories[1], y: 80 },
            { x: categories[2], y: 10 }          
          ]}
          animate={{ duration: 2000 }}
        />
      </div>
    );
  };

  render() {
    return <div>{this.renderGraph()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    tasks: Object.values(state.tasks),
    currentUserId: state.auth.UserId
  };
};

export default connect(
  mapStateToProps,
  { fetchTasks }
)(TaskGraph);
