import _ from 'lodash';
import React from "react";
import { connect } from "react-redux";
import { VictoryPie } from "victory";
import { fetchTasks } from "../../actions";

class TaskGraph extends React.Component {
  componentDidMount() {
    this.props.fetchTasks();
  }

  createCategoriesArray() {}

  renderGraph = () => {
    this.userTasks = this.props.tasks.filter(
      task => task.userId === this.props.currentUserId
    );
    this.categories = this.userTasks.map(task => task["category"]);
    this.numTasks = this.userTasks.length;
    this.running = Math.floor(
      (this.userTasks.filter(task => task["category"] === "Running").length /
        this.numTasks) *
        100
    );

    this.data = [];

    this.userTasks.map((task) => {
      this.data.push(_.pick(task, "category", "duration"));
    }); 

    console.log(this.data);

    this.totalHours = 0;

    this.userTasks.map((task) => this.totalHours += task.duration);

    if (this.props.isSignedIn) {
      return (
        <div>
          <VictoryPie
            data={[
              { x: this.categories[0], y: this.running },
              { x: this.categories[0], y: this.running }
            ]}
            colorScale={["tomato", "orange", "gold", "cyan", "navy" ]}
            innerRadius={40}
            padAngle={1}
            height={225}
          />
        </div>
      );
    } else {
      return (
        <div><h1>Please sign in.</h1></div>
      );
    }
  };

  render() {
    return <div>{this.renderGraph()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    tasks: Object.values(state.tasks),
    currentUserId: state.auth.userId,
    isSignedIn: state.auth.isSignedIn
  };
};

export default connect(
  mapStateToProps,
  { fetchTasks }
)(TaskGraph);
