import React from "react";
import { connect } from "react-redux";
import { VictoryPie, VictoryTheme } from "victory";
import { fetchTasks } from "../../actions";

class TaskGraph extends React.Component {
  componentDidMount() {
    this.props.fetchTasks();
  }

  createCategoriesArray() {}

  renderGraph = () => {
    const userTasks = this.props.tasks.filter(
      task => task.userId === this.props.currentUserId
    );
    this.categories = userTasks.map(task => task["category"]);
    this.numTasks = userTasks.length;
    this.running = Math.floor(
      (userTasks.filter(task => task["category"] === "Running").length /
        this.numTasks) *
        100
    );

    this.totalHours = 0;

    userTasks.map((task) => this.totalHours += task.duration);
    console.log(this.totalHours);

    if (!this.props.tasks) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>
          <VictoryPie
            data={[
              { x: this.categories[0], y: this.running },
              { x: this.categories[1], y: this.running }
            ]}
            colorScale={["tomato", "orange", "gold", "cyan", "navy" ]}
            innerRadius={40}
            padAngle={1}
            height={225}
          />
        </div>
      );
    }
  };

  render() {
    return <div>{this.renderGraph()}</div>;
  }
}

const mapStateToProps = state => {
  return {
    tasks: Object.values(state.tasks),
    currentUserId: state.auth.userId
  };
};

export default connect(
  mapStateToProps,
  { fetchTasks }
)(TaskGraph);
